    max-width: 98%;
  }
}

.section__title {
  font-size: 26px;
  letter-spacing: 0.5px;
  font-weight: 700;
  color: var(--primary-bg);
  text-transform: uppercase;
  margin-bottom: 2rem !important;
}

.secondaryBg {
  background: var(--secondary-bg);
}

