  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 0;
  height: 0;
  opacity: 0;
  background: var(--primary-bg);
  transition: all 300ms var(--transition-cubic);
