.header__nav-logo a {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-transform: uppercase;
  color: var(--primary-bg);
  font-weight: bold;
  font-size: 24px;
}
.header__nav-logo a:hover {
  color: var(--primary-bg);
}
.header__nav-logo a:hover span::before {
  left: 130%;
}
.header__nav-logo a:hover span::after {
  right: 130%;
}
