  font-size: 66px;
  font-weight: 600;
  letter-spacing: 0.5px;
  line-height: 80px;
}
.hero__section-content .read__more {
  display: flex;
  align-items: center;
  justify-content: center;
  border: 1px solid var(--light-color);
  color: var(--text-dark-color);
  font-size: 16px;
  font-weight: 600;
  letter-spacing: 0.5px;
}
.hero__section-content .read__more p {
  display: flex;
  align-items: center;
  text-transform: capitalize;
}
.hero__section-content .read__more p .text {
  padding: 1rem 1.5rem;
}
.hero__section-content .read__more p .icon {
  padding: 1rem 1.5rem;
  border-left: 1px solid var(--light-color);
}
.hero__section-content .read__more p .icon svg {
  font-size: 14px;
}
.hero__section-img {
  position: relative;
  margin-top: 1rem;
  margin-bottom: 5rem;
}
.hero__section-img img {
  width: 90%;
  margin-left: auto;
}

.our__mission {
  width: 100%;
