.our__mission-hero {
  width: 95%;
  padding: 5rem calc(4rem + 120px);
  position: relative;
}
.our__mission-hero::before {
  content: "";
  position: absolute;
  top: 100%;
  right: 0;
  width: 100%;
  height: 400px;
  background: var(--secondary-bg);
  z-index: -1;
}
.our__mission-hero .section__title {
  color: var(--white-color);
}
.our__mission-hero .hero__text {
  width: 70%;
}
.our__mission-hero .hero__text .hero__title {
  font-size: 44px;
  font-weight: 400;
  color: var(--white-color);
}
.our__mission-hero .hero__text .hero__desc {
  font-size: 18px;
  font-weight: 400;
  color: var(--white-color);
  margin-top: 1rem;
}
.our__mission-img {
  position: relative;
  margin-top: 1rem;
  margin-bottom: 5rem;
}
.our__mission-img img {
  width: 90%;
  margin-right: auto;
}

.latest__news {
  width: 100%;
}
.latest__news-grid {
  padding: 5rem;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-template-rows: repeat(4, 1fr);
}
